package com.demo.rabbitmq;import org.springframework.amqp.core.AmqpTemplate;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by yagu on 16/3/11. */@Service("messageProduceService")public class MessageProduceService {    @Autowired    private AmqpTemplate template;    public void pushToMessageQueue(String routingKey, String message) {        template.convertAndSend(routingKey, message);    }    public void pushToMessageQueue(String message) {        pushToMessageQueue("testQ", message);    }}